// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {TournamentCore} from "./../../TournamentCore.sol";

library TournamentCalculations {
    function calculateExitCost(
        uint256 initialCoins,
        uint32 actualStartTime,
        uint16 exitCostBasePercentBPS,
        uint16 exitCostCompoundRateBPS,
        uint32 gameInterval
    ) internal view returns (uint256) {
        uint32 currentTime = uint32(block.timestamp);

        uint256 intervalsPassed = (currentTime - actualStartTime) /
            gameInterval;
        uint256 baseCost = (initialCoins * exitCostBasePercentBPS) / 10000;
        uint256 multiplier = 10000 +
            (exitCostCompoundRateBPS * intervalsPassed);
        return (baseCost * multiplier) / 10000;
    }

    function calculateCurrentCoins(
        uint256 storedCoins,
        uint32 lastDecayTimestamp,
        uint256 decayAmount,
        uint32 gameInterval
    ) internal view returns (uint256) {
        // Cast block.timestamp to uint32 for consistent arithmetic
        uint32 currentTime = uint32(block.timestamp);

        if (currentTime <= lastDecayTimestamp) {
            return storedCoins;
        }

        uint256 intervalsPassed = (currentTime - lastDecayTimestamp) /
            gameInterval;
        uint256 totalDecay = decayAmount * intervalsPassed;
        return storedCoins > totalDecay ? storedCoins - totalDecay : 0;
    }

    function calculateForfeitPenalty(
        uint256 stakeAmount,
        uint32 endTime,
        uint32 duration,
        uint8 forfeitPenaltyType, // 0 = Fixed, 1 = TimeBased
        uint8 forfeitMaxPenalty,
        uint8 forfeitMinPenalty
    ) internal view returns (uint256) {
        uint256 penaltyPercent;

        if (
            forfeitPenaltyType == uint8(TournamentCore.ForfeitPenaltyType.Fixed)
        ) {
            // For fixed penalty, apply the minimum penalty
            penaltyPercent = forfeitMinPenalty;
        } else {
            // For time-base penalty, calculate the percentage to apply
            uint32 currentTime = uint32(block.timestamp);

            uint32 timeRemaining = endTime > currentTime
                ? endTime - currentTime
                : 0;
            penaltyPercent =
                (uint256(forfeitMaxPenalty) * timeRemaining) /
                duration;

            if (penaltyPercent < forfeitMinPenalty) {
                // Clamp to min penalty value in case calculated % is too low
                penaltyPercent = forfeitMinPenalty;
            }
            if (penaltyPercent > forfeitMaxPenalty) {
                // Clamp to max penalty value in case calculated % is too high
                penaltyPercent = forfeitMaxPenalty;
            }
        }
        return (stakeAmount * penaltyPercent) / 100;
    }

    function calculatePrizePerWinner(
        uint256 totalStaked,
        uint256 totalForfeitPenalties,
        uint8 platformFeePercent,
        uint8 creatorFeePercent,
        uint256 winnerCount
    ) internal pure returns (uint256) {
        if (winnerCount == 0) return 0;

        uint256 totalPrizePool = totalStaked + totalForfeitPenalties;
        uint256 platformFee = (totalPrizePool * platformFeePercent) / 100;
        uint256 creatorFee = (totalPrizePool * creatorFeePercent) / 100;
        uint256 distributionPool = totalPrizePool - platformFee - creatorFee;

        return distributionPool / winnerCount;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {TournamentCore} from "./../../TournamentCore.sol";
import {TournamentRegistry} from "./../../../infrastructure/registry/TournamentRegistry.sol";

library TournamentLifecycle {
    event TournamentLocked(uint32 timestamp);
    event TournamentUnlocked(uint32 timestamp);
    event TournamentPendingStart(uint32 timestamp);
    event TournamentRevertedToOpen(uint32 timestamp);
    event TournamentStarted(
        uint32 startTime,
        uint32 endTime,
        uint32 exitWindowStart
    );
    event TournamentEnded(
        uint256 winnerCount,
        uint256 prizePool,
        uint32 timestamp
    );
    event TournamentCancelled(uint32 timestamp);
    event EmergencyCancellation(
        address indexed platformAdmin,
        uint256 timestamp
    );

    function transitionToLocked(TournamentRegistry registry) internal {
        registry.updateTournamentStatus(TournamentCore.Status.Locked);
        emit TournamentLocked(uint32(block.timestamp));
    }

    function transitionToOpen(TournamentRegistry registry) internal {
        registry.updateTournamentStatus(TournamentCore.Status.Open);
        emit TournamentUnlocked(uint32(block.timestamp));
    }

    function transitionToPendingStart(TournamentRegistry registry) internal {
        registry.updateTournamentStatus(TournamentCore.Status.PendingStart);
        emit TournamentPendingStart(uint32(block.timestamp));
    }

    function transitionToActive(
        TournamentRegistry registry,
        uint32 duration,
        uint32 gameInterval
    )
        internal
        returns (uint32 actualStartTime, uint32 endTime, uint32 exitWindowStart)
    {
        actualStartTime = uint32(block.timestamp);
        endTime = actualStartTime + duration;
        exitWindowStart = endTime - gameInterval;

        registry.updateTournamentStatus(TournamentCore.Status.Active);
        emit TournamentStarted(actualStartTime, endTime, exitWindowStart);

        return (actualStartTime, endTime, exitWindowStart);
    }

    function transitionToEnded(
        TournamentRegistry registry,
        uint256 totalStaked,
        uint256 totalForfeitPenalties,
        uint256 winnerCount
    ) internal {
        registry.updateTournamentStatus(TournamentCore.Status.Ended);

        uint256 prizePool = totalStaked + totalForfeitPenalties;
        emit TournamentEnded(winnerCount, prizePool, uint32(block.timestamp));
    }

    function transitionToCancelled(TournamentRegistry registry) internal {
        registry.updateTournamentStatus(TournamentCore.Status.Cancelled);
        emit TournamentCancelled(uint32(block.timestamp));
    }

    function emergencyCancel(
        TournamentRegistry registry,
        address platformAdmin
    ) internal {
        registry.updateTournamentStatus(TournamentCore.Status.Cancelled);
        emit EmergencyCancellation(platformAdmin, block.timestamp);
    }

    function emitRevertToOpen() internal {
        emit TournamentRevertedToOpen(uint32(block.timestamp));
    }

    function checkAndTransition(
        TournamentCore.Status currentStatus,
        TournamentCore.Params memory params,
        uint16 playerCount,
        uint256 totalStaked,
        TournamentRegistry registry
    )
        external
        returns (
            TournamentCore.Status newStatus,
            uint32 actualStartTime,
            uint32 endTime,
            uint32 exitWindowStart
        )
    {
        newStatus = currentStatus;

        if (currentStatus == TournamentCore.Status.Open) {
            if (params.maxPlayers > 0 && playerCount >= params.maxPlayers) {
                newStatus = TournamentCore.Status.Locked;
                transitionToLocked(registry);
            }
        }

        if (
            currentStatus == TournamentCore.Status.Open ||
            currentStatus == TournamentCore.Status.Locked
        ) {
            if (block.timestamp >= params.startTimestamp) {
                newStatus = TournamentCore.Status.PendingStart;
                transitionToPendingStart(registry);

                if (
                    areStartConditionsMet(
                        params.startPlayerCount,
                        params.startPoolAmount,
                        playerCount,
                        totalStaked
                    )
                ) {
                    newStatus = TournamentCore.Status.Active;
                    (
                        actualStartTime,
                        endTime,
                        exitWindowStart
                    ) = transitionToActive(
                        registry,
                        params.duration,
                        params.gameInterval
                    );
                } else {
                    newStatus = TournamentCore.Status.Cancelled;
                    transitionToCancelled(registry);
                }
            }
        }
    }

    function areStartConditionsMet(
        uint16 startPlayerCount,
        uint256 startPoolAmount,
        uint16 currentPlayerCount,
        uint256 currentTotalStaked
    ) public pure returns (bool) {
        if (startPlayerCount > 0 && currentPlayerCount < startPlayerCount) {
            return false;
        }
        if (startPoolAmount > 0 && currentTotalStaked < startPoolAmount) {
            return false;
        }
        return true;
    }
}

