pragma solidity ^0.8.28;

import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {TournamentCore} from "./../../../../core/TournamentCore.sol";
import {TournamentHub} from "./../../../../modules/hub/TournamentHub.sol";
import {TournamentCombat} from "./../../../../modules/combat/TournamentCombat.sol";
import {TournamentMysteryDeck} from "./../../../../modules/mystery-deck/TournamentMysteryDeck.sol";
import {TournamentTrading} from "./../../../../modules/trading/TournamentTrading.sol";
import {TournamentRandomizer} from "./../../../../modules/randomizer/TournamentRandomizer.sol";

library TournamentFactoryDeployment {
    using Clones for address;

    struct DeployedSystem {
        address hub;
        address combat;
        address mysteryDeck;
        address trading;
        address randomizer;
    }

    struct InitConfig {
        address registry;
        address whitelist;
        address deckCatalog;
        address pythEntropy;
        address entropyProvider;
        address platformAdmin;
        address gameOracle;
    }

    error InsufficientETHForSeed();

    function deployContracts(
        address hubImpl,
        address combatImpl,
        address mysteryDeckImpl,
        address tradingImpl,
        address randomizerImpl
    ) external returns (DeployedSystem memory system) {
        system.hub = hubImpl.clone();
        system.combat = combatImpl.clone();
        system.mysteryDeck = mysteryDeckImpl.clone();
        system.trading = tradingImpl.clone();
        system.randomizer = randomizerImpl.clone();
    }

    function initializeContracts(
        DeployedSystem memory system,
        TournamentCore.Params calldata params,
        InitConfig memory config,
        address creator
    ) external {
        TournamentHub(system.hub).initialize(
            params,
            creator,
            system.combat,
            system.mysteryDeck,
            system.trading,
            system.randomizer,
            config.registry,
            config.whitelist,
            config.platformAdmin
        );

        TournamentCombat(system.combat).initialize(
            system.hub,
            config.gameOracle
        );

        TournamentMysteryDeck(system.mysteryDeck).initialize(
            system.hub,
            config.deckCatalog,
            params.excludedCardIds,
            params.deckDrawCost,
            params.deckShuffleCost,
            params.deckPeekCost,
            config.gameOracle
        );

        TournamentTrading(system.trading).initialize(
            system.hub,
            config.gameOracle
        );

        initializeRandomizer(system, config);
    }

    function initializeRandomizer(
        DeployedSystem memory system,
        InitConfig memory config
    ) public {
        TournamentRandomizer(system.randomizer).initialize(
            system.hub,
            config.pythEntropy,
            config.entropyProvider,
            config.platformAdmin,
            config.gameOracle
        );

        TournamentRandomizer(system.randomizer).setMysteryDeck(
            system.mysteryDeck
        );
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {TournamentCore} from "./../../../../core/TournamentCore.sol";
import {TournamentTokenWhitelist} from "./../../../token-whitelist/TournamentTokenWhitelist.sol";

library TournamentFactoryValidation {
    error InvalidAddress();
    error InvalidStartTimestamp();
    error InvalidCardsPerType();
    error MinPlayersInvalid();
    error MaxPlayersInvalid();
    error InvalidStartPlayerCount();
    error DurationTooShort();
    error IntervalTooShort();
    error DurationTooShortForInterval();
    error CreatorFeeTooHigh();
    error CombinedFeesTooHigh();
    error InvalidStakeToken();
    error MinStakeExceedsMaxStake();
    error InvalidDecayAmount();
    error InvalidExitCostBase();
    error InvalidCoinConversionRate();
    error InvalidInitialLives();
    error InvalidExitLivesRequired();
    error InvalidForfeitPenaltyBounds();
    error ForfeitMinPenaltyExceedsMax();
    error InvalidDeckCost();

    function validateParams(
        TournamentCore.Params calldata params,
        TournamentTokenWhitelist whitelist
    ) external view {
        if (params.coinConversionRate == 0) revert InvalidCoinConversionRate();
        if (params.decayAmount == 0) revert InvalidDecayAmount();
        if (params.initialLives == 0) revert InvalidInitialLives();
        if (params.exitLivesRequired == 0) revert InvalidExitLivesRequired();
        if (params.exitCostBasePercentBPS == 0) revert InvalidExitCostBase();
        if (params.deckDrawCost == 0) revert InvalidDeckCost();
        if (params.deckShuffleCost == 0) revert InvalidDeckCost();
        if (params.deckPeekCost == 0) revert InvalidDeckCost();
        if (params.deckCatalog == address(0)) revert InvalidAddress();

        validateTimingParams(params);
        validatePlayerParams(params);
        validateEconomicParams(params, whitelist);
    }

    function validateTimingParams(
        TournamentCore.Params calldata params
    ) public view {
        if (params.startTimestamp <= block.timestamp)
            revert InvalidStartTimestamp();
        if (params.duration < TournamentCore.MIN_DURATION)
            revert DurationTooShort();
        if (params.gameInterval < TournamentCore.MIN_GAME_INTERVAL)
            revert IntervalTooShort();

        uint256 maxIntervals = params.duration / params.gameInterval;
        if (maxIntervals < TournamentCore.MIN_INTERVALS_REQUIRED)
            revert DurationTooShortForInterval();

        if (params.decayAmount > type(uint128).max / maxIntervals) {
            revert InvalidDecayAmount();
        }
    }

    function validatePlayerParams(
        TournamentCore.Params calldata params
    ) public pure {
        if (params.minPlayers < TournamentCore.MIN_PLAYERS_REQUIRED)
            revert MinPlayersInvalid();

        if (
            params.minPlayers >= TournamentCore.MIN_PLAYERS_REQUIRED &&
            params.startPlayerCount < params.minPlayers
        ) {
            revert InvalidStartPlayerCount();
        }

        if (params.maxPlayers > 0) {
            if (params.maxPlayers < params.minPlayers)
                revert MaxPlayersInvalid();
            if (params.startPlayerCount > params.maxPlayers)
                revert InvalidStartPlayerCount();
        }

        if (params.cardsPerType < TournamentCore.MIN_CARDS_PER_TYPE)
            revert InvalidCardsPerType();

        unchecked {
            if (params.cardsPerType * 3 > 255) revert InvalidCardsPerType();
        }
    }

    function validateEconomicParams(
        TournamentCore.Params calldata params,
        TournamentTokenWhitelist whitelist
    ) public view {
        if (
            params.minStake > 0 &&
            params.maxStake > 0 &&
            params.minStake > params.maxStake
        ) revert MinStakeExceedsMaxStake();

        if (params.creatorFeePercent > TournamentCore.MAX_CREATOR_FEE_PERCENT)
            revert CreatorFeeTooHigh();
        if (
            params.creatorFeePercent + params.platformFeePercent >
            TournamentCore.MAX_COMBINED_FEE_PERCENT
        ) revert CombinedFeesTooHigh();

        if (params.forfeitAllowed) {
            if (
                params.forfeitMinPenalty > 100 || params.forfeitMaxPenalty > 100
            ) revert InvalidForfeitPenaltyBounds();
            if (params.forfeitMinPenalty > params.forfeitMaxPenalty)
                revert ForfeitMinPenaltyExceedsMax();
        } else {
            if (params.forfeitMinPenalty > 0 || params.forfeitMaxPenalty > 0)
                revert InvalidForfeitPenaltyBounds();
        }

        if (!whitelist.isWhitelisted(params.stakeToken))
            revert InvalidStakeToken();
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {TournamentRegistry} from "./../registry/TournamentRegistry.sol";
import {TournamentTokenWhitelist} from "./../token-whitelist/TournamentTokenWhitelist.sol";
import {TournamentDeckCatalog} from "./../deck-catalog/TournamentDeckCatalog.sol";
import {TournamentCore} from "./../../core/TournamentCore.sol";
import {TournamentFactoryValidation} from "./libraries/validation/FactoryValidation.sol";
import {TournamentFactoryDeployment} from "./libraries/deployment/FactoryDeployment.sol";

contract TournamentFactory is Ownable {
    struct FactoryConfig {
        address hubImpl;
        address combatImpl;
        address mysteryDeckImpl;
        address tradingImpl;
        address randomizerImpl;
        address registry;
        address whitelist;
        address deckCatalog;
        address pythEntropy;
        address entropyProvider;
        address platformAdmin;
        address gameOracle;
        uint8 platformFeePercent;
    }

    address public immutable hubImplementation;
    address public immutable combatImplementation;
    address public immutable mysteryDeckImplementation;
    address public immutable tradingImplementation;
    address public immutable randomizerImplementation;

    address public immutable platformAdmin;
    TournamentRegistry public immutable registry;
    TournamentTokenWhitelist public immutable whitelist;
    TournamentDeckCatalog public immutable deckCatalog;

    address public immutable pythEntropy;
    address public immutable entropyProvider;
    uint8 public platformFeePercent;

    address public gameOracle;

    event TournamentSystemCreated(
        address indexed hub,
        address indexed combat,
        address mysteryDeck,
        address trading,
        address randomizer,
        address indexed creator,
        TournamentCore.Params params
    );

    event PlatformFeeUpdated(uint8 newFee);
    event RngOracleUpdated(address indexed newOracle);
    event ETHDeposited(address indexed from, uint256 amount);
    event ETHWithdrawn(address indexed to, uint256 amount);

    error InvalidAddress();
    error PlatformFeeTooHigh();

    constructor(FactoryConfig memory config) Ownable(msg.sender) {
        if (
            config.hubImpl == address(0) ||
            config.combatImpl == address(0) ||
            config.mysteryDeckImpl == address(0) ||
            config.tradingImpl == address(0) ||
            config.randomizerImpl == address(0) ||
            config.registry == address(0) ||
            config.whitelist == address(0) ||
            config.deckCatalog == address(0) ||
            config.pythEntropy == address(0) ||
            config.entropyProvider == address(0)
        ) revert InvalidAddress();

        if (config.platformFeePercent > 5) revert PlatformFeeTooHigh();

        hubImplementation = config.hubImpl;
        combatImplementation = config.combatImpl;
        mysteryDeckImplementation = config.mysteryDeckImpl;
        tradingImplementation = config.tradingImpl;
        randomizerImplementation = config.randomizerImpl;
        registry = TournamentRegistry(config.registry);
        whitelist = TournamentTokenWhitelist(config.whitelist);
        deckCatalog = TournamentDeckCatalog(config.deckCatalog);
        pythEntropy = config.pythEntropy;
        entropyProvider = config.entropyProvider;

        platformAdmin = config.platformAdmin == address(0)
            ? msg.sender
            : config.platformAdmin;
        gameOracle = config.gameOracle == address(0)
            ? msg.sender
            : config.gameOracle;
        platformFeePercent = config.platformFeePercent;
    }

    receive() external payable {
        emit ETHDeposited(msg.sender, msg.value);
    }

    function createTournamentSystem(
        TournamentCore.Params calldata params
    ) external returns (address hub) {
        TournamentFactoryValidation.validateParams(params, whitelist);

        TournamentFactoryDeployment.DeployedSystem
            memory system = TournamentFactoryDeployment.deployContracts(
                hubImplementation,
                combatImplementation,
                mysteryDeckImplementation,
                tradingImplementation,
                randomizerImplementation
            );

        TournamentFactoryDeployment.InitConfig
            memory initConfig = TournamentFactoryDeployment.InitConfig({
                registry: address(registry),
                whitelist: address(whitelist),
                deckCatalog: address(deckCatalog),
                pythEntropy: pythEntropy,
                entropyProvider: entropyProvider,
                platformAdmin: platformAdmin,
                gameOracle: gameOracle
            });

        TournamentFactoryDeployment.initializeContracts(
            system,
            params,
            initConfig,
            msg.sender
        );

        // Register
        registry.registerTournamentSystem(
            system.hub,
            system.combat,
            system.mysteryDeck,
            system.trading,
            system.randomizer
        );

        emit TournamentSystemCreated(
            system.hub,
            system.combat,
            system.mysteryDeck,
            system.trading,
            system.randomizer,
            msg.sender,
            params
        );

        return system.hub;
    }

    function setPlatformFee(uint8 newFee) external onlyOwner {
        if (newFee > TournamentCore.MAX_PLATFORM_FEE)
            revert PlatformFeeTooHigh();
        platformFeePercent = newFee;
        emit PlatformFeeUpdated(newFee);
    }

    function setGameOracle(address newOracle) external onlyOwner {
        if (newOracle == address(0)) revert InvalidAddress();
        gameOracle = newOracle;
        emit RngOracleUpdated(newOracle);
    }

    function withdrawETH(uint256 amount) external onlyOwner {
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "ETH transfer failed");
        emit ETHWithdrawn(msg.sender, amount);
    }

    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    function getImplementations()
        external
        view
        returns (
            address hub,
            address combat,
            address mysteryDeck,
            address trading,
            address randomizer
        )
    {
        return (
            hubImplementation,
            combatImplementation,
            mysteryDeckImplementation,
            tradingImplementation,
            randomizerImplementation
        );
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/Ownable.sol";
import {TournamentCore} from "./../../core/TournamentCore.sol";

/**
 * Architecture:
 * - Each tournament = 5 contracts (Hub and 4 modules: Combat, Deck, Trading, Randomizer)
 * - Hub is the primary contract - owns player state, updates status
 * - Registry tracks complete systems and provides reverse lookups
 */
contract TournamentRegistry is Ownable {
    struct TournamentSystem {
        address hub;
        address combat;
        address mysteryDeck;
        address trading;
        address randomizer;
        bool exists;
    }

    mapping(address => bool) private _hasFactoryRole;

    // Tournament tracking (by hub address - primary)
    address[] private _allTournaments;
    mapping(address => TournamentSystem) private _tournamentSystems;
    mapping(address => bool) private _isRegistered;

    // Status tracking (by hub address)
    mapping(address => TournamentCore.Status) private _tournamentStatus;
    mapping(TournamentCore.Status => address[]) private _tournamentsByStatus;
    mapping(address => mapping(TournamentCore.Status => uint256))
        private _tournamentStatusIndex;

    // Reverse lookups (module address => hub address)
    mapping(address => address) private _moduleToHub;

    event FactoryRoleGranted(address indexed factory);
    event FactoryRoleRevoked(address indexed factory);
    event TournamentSystemRegistered(
        address indexed hub,
        address indexed combat,
        address mysteryDeck,
        address trading,
        address randomizer,
        TournamentCore.Status initialStatus
    );
    event TournamentStatusUpdated(
        address indexed hub,
        TournamentCore.Status oldStatus,
        TournamentCore.Status newStatus
    );

    error InvalidAddress();
    error OnlyFactory();
    error AlreadyRegistered();
    error NotRegistered();
    error ModuleAlreadyUsed();

    modifier onlyRegisteredHub(address hub) {
        if (!_isRegistered[hub]) revert NotRegistered();
        _;
    }

    constructor() Ownable(msg.sender) {}

    function grantFactoryRole(address factory) external onlyOwner {
        if (factory == address(0)) revert InvalidAddress();
        _hasFactoryRole[factory] = true;
        emit FactoryRoleGranted(factory);
    }

    function revokeFactoryRole(address factory) external onlyOwner {
        _hasFactoryRole[factory] = false;
        emit FactoryRoleRevoked(factory);
    }

    function hasFactoryRole(address factory) external view returns (bool) {
        return _hasFactoryRole[factory];
    }

    /**
     * @notice Register a complete tournament system (5 contracts)
     * @dev Called by TournamentFactory after deploying all 5 minimal proxies
     * Validates all addresses and ensures no module is reused across tournaments
     */
    function registerTournamentSystem(
        address hub,
        address combat,
        address mysteryDeck,
        address trading,
        address randomizer
    ) external {
        if (!_hasFactoryRole[msg.sender]) revert OnlyFactory();
        if (_isRegistered[hub]) revert AlreadyRegistered();
        if (
            hub == address(0) ||
            combat == address(0) ||
            randomizer == address(0) ||
            trading == address(0) ||
            mysteryDeck == address(0)
        ) revert InvalidAddress();

        if (
            _moduleToHub[combat] != address(0) ||
            _moduleToHub[randomizer] != address(0) ||
            _moduleToHub[mysteryDeck] != address(0) ||
            _moduleToHub[trading] != address(0)
        ) revert ModuleAlreadyUsed();

        _tournamentSystems[hub] = TournamentSystem({
            hub: hub,
            combat: combat,
            mysteryDeck: mysteryDeck,
            trading: trading,
            randomizer: randomizer,
            exists: true
        });

        _isRegistered[hub] = true;
        _allTournaments.push(hub);

        // Setup reverse lookup
        _moduleToHub[hub] = hub; // Hub points to itself
        _moduleToHub[combat] = hub;
        _moduleToHub[mysteryDeck] = hub;
        _moduleToHub[trading] = hub;
        _moduleToHub[randomizer] = hub;

        // Status tracking
        TournamentCore.Status initialStatus = TournamentCore.Status.Open;
        _tournamentStatus[hub] = initialStatus;

        uint256 statusArrayLength = _tournamentsByStatus[initialStatus].length;
        _tournamentStatusIndex[hub][initialStatus] = statusArrayLength;
        _tournamentsByStatus[initialStatus].push(hub);

        emit TournamentSystemRegistered(
            hub,
            combat,
            mysteryDeck,
            trading,
            randomizer,
            initialStatus
        );
    }

    function updateTournamentStatus(TournamentCore.Status newStatus) external {
        address hub = msg.sender;

        if (!_isRegistered[hub]) revert NotRegistered();

        TournamentCore.Status oldStatus = _tournamentStatus[hub];

        // Early exit if status hasn't changed
        if (oldStatus == newStatus) return;

        // Remove from old status array
        _removeFromStatusArray(hub, oldStatus);

        // Add to new status array
        uint256 newStatusArrayLength = _tournamentsByStatus[newStatus].length;
        _tournamentStatusIndex[hub][newStatus] = newStatusArrayLength;
        _tournamentsByStatus[newStatus].push(hub);
        _tournamentStatus[hub] = newStatus;

        emit TournamentStatusUpdated(hub, oldStatus, newStatus);
    }

    function _removeFromStatusArray(
        address hub,
        TournamentCore.Status status
    ) private onlyRegisteredHub(hub) {
        uint256 indexToRemove = _tournamentStatusIndex[hub][status];

        unchecked {
            // Safe: array length is always > 0 when removing (hub is registered in this status)
            uint256 lastIndex = _tournamentsByStatus[status].length - 1;

            if (indexToRemove != lastIndex) {
                address lastTournament = _tournamentsByStatus[status][
                    lastIndex
                ];
                _tournamentsByStatus[status][indexToRemove] = lastTournament;
                _tournamentStatusIndex[lastTournament][status] = indexToRemove;
            }

            _tournamentsByStatus[status].pop();
        }
    }

    /**
     * @notice Get complete tournament system by hub address
     */
    function getTournamentSystem(
        address hub
    ) external view onlyRegisteredHub(hub) returns (TournamentSystem memory) {
        return _tournamentSystems[hub];
    }

    /**
     * @notice Get hub address from any module address
     * @dev Useful when you have a Combat/Deck/Trading/Randomizer address
     */
    function getHubAddress(
        address moduleAddress
    ) external view returns (address) {
        address hub = _moduleToHub[moduleAddress];
        if (hub == address(0)) revert NotRegistered();
        return hub;
    }

    /**
     * @notice Get tournament system by any module address
     */
    function getTournamentSystemByModule(
        address moduleAddress
    ) external view returns (TournamentSystem memory) {
        address hub = _moduleToHub[moduleAddress];
        if (hub == address(0)) revert NotRegistered();
        return _tournamentSystems[hub];
    }

    /**
     * @notice Get all tournament hub addresses
     */
    function getAllTournaments() external view returns (address[] memory) {
        return _allTournaments;
    }

    /**
     * @notice Get tournaments by status (returns hub addresses)
     */
    function getTournamentsByStatus(
        TournamentCore.Status status
    ) external view returns (address[] memory) {
        return _tournamentsByStatus[status];
    }

    function getTournamentStatus(
        address hub
    ) external view onlyRegisteredHub(hub) returns (uint8) {
        return uint8(_tournamentStatus[hub]);
    }

    function isTournamentRegistered(address hub) external view returns (bool) {
        return _isRegistered[hub];
    }

    function isModuleRegistered(
        address moduleAddress
    ) external view returns (bool) {
        return _moduleToHub[moduleAddress] != address(0);
    }

    function getTournamentCount() external view returns (uint256) {
        return _allTournaments.length;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/Ownable.sol";

contract TournamentTokenWhitelist is Ownable {
    struct TokenInfo {
        bool exists;
        bool isPaused;
        uint8 index;
    }

    uint8 public constant MAX_TOKENS = 255;

    address[] private _tokens;
    mapping(address => TokenInfo) private _tokenInfo;

    event TokenWhitelisted(address indexed token);
    event TokenPaused(address indexed token, string reason);
    event TokenUnpaused(address indexed token);

    error InvalidTokenAddress();
    error AlreadyExists();
    error NotFound();
    error MaxTokensReached();

    constructor() Ownable(msg.sender) {}

    function addToken(address token) external onlyOwner {
        if (token == address(0)) revert InvalidTokenAddress();

        TokenInfo storage info = _tokenInfo[token];
        if (info.exists) revert AlreadyExists();

        uint256 currentLength = _tokens.length;
        if (currentLength >= MAX_TOKENS) revert MaxTokensReached();

        info.exists = true;
        info.isPaused = false;
        info.index = uint8(currentLength);

        _tokens.push(token);

        emit TokenWhitelisted(token);
    }

    function pauseToken(
        address token,
        string calldata reason
    ) external onlyOwner {
        TokenInfo storage info = _tokenInfo[token];
        if (!info.exists) revert NotFound();

        info.isPaused = true;
        emit TokenPaused(token, reason);
    }

    function unpauseToken(address token) external onlyOwner {
        TokenInfo storage info = _tokenInfo[token];
        if (!info.exists) revert NotFound();

        info.isPaused = false;
        emit TokenUnpaused(token);
    }

    function isWhitelisted(address token) external view returns (bool) {
        TokenInfo storage info = _tokenInfo[token];
        return info.exists && !info.isPaused;
    }

    function isPaused(address token) external view returns (bool) {
        return _tokenInfo[token].isPaused;
    }

    function isTokenRegistered(address token) external view returns (bool) {
        return _tokenInfo[token].exists;
    }

    function getTokens() external view returns (address[] memory) {
        return _tokens;
    }

    function getTokenCount() external view returns (uint256) {
        return _tokens.length;
    }

    function getRemainingCapacity() external view returns (uint256) {
        unchecked {
            return MAX_TOKENS - _tokens.length;
        }
    }

    function getToken(
        address token
    ) external view returns (bool whitelisted, bool paused, uint8 index) {
        TokenInfo storage info = _tokenInfo[token];
        return (info.exists, info.isPaused, info.index);
    }
}

