// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {IEntropyConsumer} from "@pythnetwork/entropy-sdk-solidity/IEntropyConsumer.sol";
import {IEntropy} from "@pythnetwork/entropy-sdk-solidity/IEntropy.sol";

contract TournamentRandomizer is Initializable, IEntropyConsumer {
    address public hub;
    address public mysteryDeck;
    address public platformAdmin;
    IEntropy public entropy;
    address public entropyProvider;

    uint256 public seedCount;
    bytes32 public revealedCompleteSeed;
    bytes32 public revealedBackendSecret;
    bool public isRevealed;

    mapping(address => bool) public hasOracleRole;
    mapping(uint64 => SeedRequest) public seedRequests;

    struct SeedRequest {
        address requester;
        uint32 timestamp;
        bool fulfilled;
        bool cancelled;
    }

    uint32 public constant SEED_REQUEST_TIMEOUT = 3600;

    event SeedRequested(
        uint64 indexed sequenceNumber,
        address indexed requester,
        uint256 seedIndex,
        uint32 timestamp
    );

    event SeedGenerated(
        uint64 indexed sequenceNumber,
        uint256 seedIndex,
        bytes32 indexed seed,
        uint32 timestamp
    );

    event CompleteSeedRevealed(
        bytes32 completeSeed,
        bytes32 backendSecret,
        uint32 timestamp
    );

    event SeedRequestCancelled(uint64 indexed sequenceNumber, uint32 timestamp);

    event OracleRoleGranted(address indexed oracle);
    event OracleRoleRevoked(address indexed oracle);
    event MysteryDeckSet(address indexed mysteryDeck);

    error InvalidAddress();
    error RandomizerUnauthorized();
    error RandomizerOnlyPlatformAdmin();
    error RandomizerOnlyOracle();
    error RandomizerOnlyHub();
    error RandomizerMysteryDeckAlreadySet();

    error TournamentNotEnded();
    error AlreadyRevealed();
    error RequestNotFound();
    error RequestAlreadyFulfilled();
    error RequestAlreadyCancelled();
    error TimeoutNotReached();
    error InsufficientFee();

    modifier onlyPlatformAdmin() {
        if (msg.sender != platformAdmin) revert RandomizerOnlyPlatformAdmin();
        _;
    }

    modifier onlyOracle() {
        if (!hasOracleRole[msg.sender]) revert RandomizerOnlyOracle();
        _;
    }

    modifier onlyAuthorizedCaller() {
        if (msg.sender != hub && msg.sender != mysteryDeck) {
            revert RandomizerUnauthorized();
        }
        _;
    }

    function initialize(
        address _hub,
        address _pythEntropy,
        address _entropyProvider,
        address _admin,
        address _oracle
    ) external initializer {
        if (
            _hub == address(0) ||
            _pythEntropy == address(0) ||
            _entropyProvider == address(0)
        ) {
            revert InvalidAddress();
        }

        hub = _hub;
        entropy = IEntropy(_pythEntropy);
        entropyProvider = _entropyProvider;
        platformAdmin = _admin == address(0) ? msg.sender : _admin;

        if (_oracle != address(0)) {
            hasOracleRole[_oracle] = true;
            emit OracleRoleGranted(_oracle);
        }
    }

    function setMysteryDeck(address _mysteryDeck) external {
        if (_mysteryDeck == address(0)) revert InvalidAddress();
        if (mysteryDeck != address(0)) revert RandomizerMysteryDeckAlreadySet();

        mysteryDeck = _mysteryDeck;
        emit MysteryDeckSet(_mysteryDeck);
    }

    function requestSeed(
        address requester
    ) external payable onlyAuthorizedCaller returns (uint64 sequenceNumber) {
        uint256 fee = entropy.getFee(entropyProvider);
        if (msg.value < fee) revert InsufficientFee();

        bytes32 userRandomNumber = keccak256(
            abi.encodePacked(
                block.timestamp,
                block.prevrandao,
                requester,
                seedCount
            )
        );

        sequenceNumber = entropy.request{value: fee}(
            entropyProvider,
            userRandomNumber,
            true
        );

        seedRequests[sequenceNumber] = SeedRequest({
            requester: requester,
            timestamp: uint32(block.timestamp),
            fulfilled: false,
            cancelled: false
        });

        emit SeedRequested(
            sequenceNumber,
            requester,
            seedCount,
            uint32(block.timestamp)
        );

        return sequenceNumber;
    }

    function entropyCallback(
        uint64 sequenceNumber,
        address,
        bytes32 randomNumber
    ) internal override {
        SeedRequest storage request = seedRequests[sequenceNumber];

        if (request.timestamp == 0) return;
        if (request.fulfilled || request.cancelled) return;

        request.fulfilled = true;

        unchecked {
            seedCount++;
        }

        emit SeedGenerated(
            sequenceNumber,
            seedCount - 1,
            randomNumber,
            uint32(block.timestamp)
        );
    }

    function cancelFailedSeedRequest(uint64 sequenceNumber) external {
        SeedRequest storage request = seedRequests[sequenceNumber];

        if (request.timestamp == 0) revert RequestNotFound();
        if (request.fulfilled) revert RequestAlreadyFulfilled();
        if (request.cancelled) revert RequestAlreadyCancelled();
        if (block.timestamp < request.timestamp + SEED_REQUEST_TIMEOUT) {
            revert TimeoutNotReached();
        }

        request.cancelled = true;

        if (seedCount == 0) {
            ITournamentHub(hub).handleFailedRandomness();
        }

        emit SeedRequestCancelled(sequenceNumber, uint32(block.timestamp));
    }

    function revealCompleteSeed(
        bytes32 seed,
        bytes32 secret
    ) external onlyOracle {
        if (isRevealed) revert AlreadyRevealed();

        (bool success, bytes memory data) = hub.staticcall(
            abi.encodeWithSignature("status()")
        );
        if (!success) revert TournamentNotEnded();

        uint8 status = abi.decode(data, (uint8));
        if (status != 4) revert TournamentNotEnded();

        (success, data) = hub.staticcall(abi.encodeWithSignature("endTime()"));
        if (!success) revert TournamentNotEnded();

        uint32 endTime = abi.decode(data, (uint32));
        if (block.timestamp < endTime) revert TournamentNotEnded();

        isRevealed = true;
        revealedCompleteSeed = seed;
        revealedBackendSecret = secret;

        emit CompleteSeedRevealed(seed, secret, uint32(block.timestamp));
    }

    function grantOracleRole(address o) external onlyPlatformAdmin {
        if (o == address(0)) revert InvalidAddress();
        hasOracleRole[o] = true;
        emit OracleRoleGranted(o);
    }

    function revokeOracleRole(address o) external onlyPlatformAdmin {
        hasOracleRole[o] = false;
        emit OracleRoleRevoked(o);
    }

    function getFee() external view returns (uint256) {
        return entropy.getFee(entropyProvider);
    }

    function getSeedCount() external view returns (uint256) {
        return seedCount;
    }

    function getSeedRequest(
        uint64 sequenceNumber
    ) external view returns (SeedRequest memory) {
        return seedRequests[sequenceNumber];
    }

    function getEntropy() internal view override returns (address) {
        return address(entropy);
    }
}

interface ITournamentHub {
    function handleFailedRandomness() external;
    function status() external view returns (uint8);
    function endTime() external view returns (uint32);
}

project/contracts/tournament/modules/tra.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {TournamentCore} from "./../../core/TournamentCore.sol";

interface ITournamentHub {
    function getPlayer(
        address player
    ) external view returns (TournamentCore.PlayerResources memory);
    function updatePlayerResources(
        address player,
        TournamentCore.PlayerResources calldata resources
    ) external;
    function getParams() external view returns (TournamentCore.Params memory);
    function status() external view returns (TournamentCore.Status);
}

contract TournamentTrading is Initializable {
    address public hub;
    address public gameOracle;

    uint256 public nextOfferId;
    uint256 public totalTradesExecuted;

    enum OfferStatus {
        Open,
        Cancelled,
        Executed
    }

    struct ResourceBundle {
        uint8 lives;
        uint256 coins;
        uint8 rockCards;
        uint8 paperCards;
        uint8 scissorsCards;
    }

    struct TradeOffer {
        address creator;
        uint32 expiresAt;
        OfferStatus status;
        bool exists;
        ResourceBundle offered;
        ResourceBundle requested;
    }

    mapping(uint256 => TradeOffer) public offers;
    mapping(address => uint256[]) private playerOffers;

    event OfferCreated(
        uint256 indexed offerId,
        address indexed creator,
        ResourceBundle offered,
        ResourceBundle requested,
        uint32 expiresAt,
        uint32 createdAt
    );

    event OfferCancelled(
        uint256 indexed offerId,
        address indexed creator,
        uint32 timestamp
    );

    event TradeExecuted(
        uint256 indexed offerId,
        address indexed creator,
        address indexed acceptor,
        uint8 creatorTotalCardsDelta,
        uint8 acceptorTotalCardsDelta,
        uint32 timestamp
    );

    error InvalidAddress();
    error OnlyGameOracle();
    error PlayerNotFound();
    error PlayerNotActive();
    error PlayerInCombat();
    error OfferNotFound();
    error OfferExpired();
    error OfferNotActive();
    error NotOfferCreator();
    error InsufficientResources();
    error ResourceOverflow();
    error TournamentNotActive();
    error InvalidCardDeltas();

    modifier onlyGameOracle() {
        if (msg.sender != gameOracle) revert OnlyGameOracle();
        _;
    }

    modifier onlyActivePlayer() {
        TournamentCore.PlayerResources memory player = ITournamentHub(hub)
            .getPlayer(msg.sender);
        if (!player.exists) revert PlayerNotFound();
        if (player.status != TournamentCore.PlayerStatus.Active)
            revert PlayerNotActive();
        if (player.inCombat) revert PlayerInCombat();
        _;
    }

    modifier tournamentActive() {
        if (ITournamentHub(hub).status() != TournamentCore.Status.Active)
            revert TournamentNotActive();
        _;
    }

    function initialize(
        address _hub,
        address _gameOracle
    ) external initializer {
        if (_hub == address(0) || _gameOracle == address(0))
            revert InvalidAddress();
        hub = _hub;
        gameOracle = _gameOracle;
        nextOfferId = 1;
    }

    function createOffer(
        ResourceBundle calldata offered,
        ResourceBundle calldata requested
    ) external onlyActivePlayer tournamentActive returns (uint256 offerId) {
        TournamentCore.Params memory params = ITournamentHub(hub).getParams();
        uint32 expiresAt = uint32(block.timestamp) + params.gameInterval;

        offerId = nextOfferId;
        unchecked {
            nextOfferId++;
        }

        offers[offerId] = TradeOffer({
            creator: msg.sender,
            expiresAt: expiresAt,
            status: OfferStatus.Open,
            exists: true,
            offered: offered,
            requested: requested
        });

        playerOffers[msg.sender].push(offerId);

        emit OfferCreated(
            offerId,
            msg.sender,
            offered,
            requested,
            expiresAt,
            uint32(block.timestamp)
        );
    }

    function cancelOffer(
        uint256 offerId
    ) external onlyActivePlayer tournamentActive {
        TradeOffer storage offer = offers[offerId];

        if (!offer.exists) revert OfferNotFound();
        if (offer.creator != msg.sender) revert NotOfferCreator();
        if (offer.status != OfferStatus.Open) revert OfferNotActive();

        offer.status = OfferStatus.Cancelled;

        emit OfferCancelled(offerId, msg.sender, uint32(block.timestamp));
    }

    function executeTradeResolution(
        uint256 offerId,
        address acceptor,
        uint8 creatorTotalCardsDelta,
        uint8 acceptorTotalCardsDelta
    ) external onlyGameOracle tournamentActive {
        TradeOffer storage offer = offers[offerId];

        if (!offer.exists) revert OfferNotFound();
        if (offer.status != OfferStatus.Open) revert OfferNotActive();
        if (block.timestamp >= offer.expiresAt) revert OfferExpired();

        // Validate deltas match bundles
        uint8 offeredTotal = offer.offered.rockCards +
            offer.offered.paperCards +
            offer.offered.scissorsCards;
        uint8 requestedTotal = offer.requested.rockCards +
            offer.requested.paperCards +
            offer.requested.scissorsCards;

        if (
            creatorTotalCardsDelta != offeredTotal ||
            acceptorTotalCardsDelta != requestedTotal
        ) revert InvalidCardDeltas();

        TournamentCore.PlayerResources memory creator = ITournamentHub(hub)
            .getPlayer(offer.creator);
        TournamentCore.PlayerResources memory acceptorData = ITournamentHub(hub)
            .getPlayer(acceptor);

        if (!creator.exists || !acceptorData.exists) revert PlayerNotFound();
        if (
            creator.status != TournamentCore.PlayerStatus.Active ||
            acceptorData.status != TournamentCore.PlayerStatus.Active
        ) revert PlayerNotActive();
        if (creator.inCombat || acceptorData.inCombat) revert PlayerInCombat();

        if (creator.lives < offer.offered.lives) revert InsufficientResources();
        if (creator.coins < offer.offered.coins) revert InsufficientResources();
        if (creator.totalCards < creatorTotalCardsDelta)
            revert InsufficientResources();

        if (acceptorData.lives < offer.requested.lives)
            revert InsufficientResources();
        if (acceptorData.coins < offer.requested.coins)
            revert InsufficientResources();
        if (acceptorData.totalCards < acceptorTotalCardsDelta)
            revert InsufficientResources();

        // Validate uint8 overflow on additions
        if (
            creator.totalCards -
                creatorTotalCardsDelta +
                acceptorTotalCardsDelta >
            type(uint8).max
        ) revert ResourceOverflow();
        if (
            acceptorData.totalCards -
                acceptorTotalCardsDelta +
                creatorTotalCardsDelta >
            type(uint8).max
        ) revert ResourceOverflow();

        creator.lives = _sub(creator.lives, offer.offered.lives);
        creator.coins -= offer.offered.coins;
        creator.totalCards -= creatorTotalCardsDelta;

        creator.lives = _add(creator.lives, offer.requested.lives);
        creator.coins += offer.requested.coins;
        creator.totalCards += acceptorTotalCardsDelta;

        acceptorData.lives = _sub(acceptorData.lives, offer.requested.lives);
        acceptorData.coins -= offer.requested.coins;
        acceptorData.totalCards -= acceptorTotalCardsDelta;

        acceptorData.lives = _add(acceptorData.lives, offer.offered.lives);
        acceptorData.coins += offer.offered.coins;
        acceptorData.totalCards += creatorTotalCardsDelta;

        offer.status = OfferStatus.Executed;
        unchecked {
            totalTradesExecuted++;
        }

        ITournamentHub(hub).updatePlayerResources(offer.creator, creator);
        ITournamentHub(hub).updatePlayerResources(acceptor, acceptorData);

        emit TradeExecuted(
            offerId,
            offer.creator,
            acceptor,
            creatorTotalCardsDelta,
            acceptorTotalCardsDelta,
            uint32(block.timestamp)
        );
    }

    function _sub(uint8 a, uint8 b) internal pure returns (uint8) {
        return a >= b ? a - b : 0;
    }

    function _add(uint8 a, uint8 b) internal pure returns (uint8) {
        uint256 result = uint256(a) + uint256(b);
        if (result > type(uint8).max) revert ResourceOverflow();
        return uint8(result);
    }

    function getOffer(
        uint256 offerId
    ) external view returns (TradeOffer memory) {
        return offers[offerId];
    }

    function getOfferStatus(
        uint256 offerId
    ) external view returns (OfferStatus) {
        if (!offers[offerId].exists) revert OfferNotFound();
        return offers[offerId].status;
    }

    function getPlayerOffers(
        address player
    ) external view returns (uint256[] memory) {
        return playerOffers[player];
    }

    function isOfferActive(uint256 offerId) external view returns (bool) {
        TradeOffer memory offer = offers[offerId];
        return
            offer.exists &&
            offer.status == OfferStatus.Open &&
            block.timestamp < offer.expiresAt;
    }
}
